var Site={init:function(){this.tabs();},tabs:function(){var tabs=$('#secondary > .tabs'),uls=tabs.find('> ul'),tabHeadings=tabs.prev('#tabHeadings');tabHeadings.delegate('li','click',function(e){var li=$(this),hash;li.siblings().removeClass('selected').end().addClass('selected');hash=li.children('a').attr('href');uls.hide().filter(hash).fadeIn(500);e.preventDefault();});}}
Site.init();